
public class LeftandRightShiftOperatos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=121;           //binary format of 121=1111001
int b=a>>3;          //It will decrease by 3 times=1111
                     //Regular format of 1111=15
System.out.println(b);



int a1=158;           //binary format of 158=10011110
int b1=a1<<3;          //It will increase by 3 times=10011110000
                     //Regular format of 10011110000=1264
System.out.println(b1);

	}

}
